import React from "react"
import PropTypes from "prop-types"
import { Manager } from "react-popper"
import classNames from "classnames"
import { DropdownContext } from "./DropdownContext"
import { mapToCssModules, omit, keyCodes, tagPropType } from "./utils"
import DropdownItem from "./DropdownItem"
import DropdownMenu from "./DropdownMenu"
import DropdownToggle from "./DropdownToggle"
import DropdownHeader from "./DropdownHeader"
import DropdownDivider from "./DropdownDivider"
import DropdownSubmenu from "./DropdownSubmenu"
import DropdownSubmenuMenu from "./DropdownSubmenuMenu"
import DropdownRow from "./DropdownRow"
import DropdownCol from "./DropdownCol"

const propTypes = {
  setActiveFromChild: PropTypes.bool,
  className: PropTypes.string,
  cssModule: PropTypes.object,
  addonType: PropTypes.oneOfType([PropTypes.bool, PropTypes.oneOf(["prepend", "append"])]),
  direction: PropTypes.oneOf(["up", "down", "left", "right"]),
  disabled: PropTypes.bool,
  children: PropTypes.node,
  isOpen: PropTypes.bool,
  active: PropTypes.bool,
  toggle: PropTypes.func,
  group: PropTypes.bool,
  a11y: PropTypes.bool,
  size: PropTypes.string,
  nav: PropTypes.bool,
  tag: tagPropType
}

const defaultProps = {
  setActiveFromChild: false,
  direction: "down",
  addonType: false,
  active: false,
  isOpen: false,
  a11y: true,
  nav: false
}

const preventDefaultKeys = [
  keyCodes.space,
  keyCodes.enter,
  keyCodes.up,
  keyCodes.down,
  keyCodes.end,
  keyCodes.home
]

const omitKeys = ["defaultOpen"]

class Dropdown extends React.Component {
  constructor(props) {
    super(props)

    this.addEvents = this.addEvents.bind(this)
    this.handleDocumentClick = this.handleDocumentClick.bind(this)
    this.handleKeyDown = this.handleKeyDown.bind(this)
    this.removeEvents = this.removeEvents.bind(this)
    this.toggle = this.toggle.bind(this)

    this.containerRef = React.createRef()
  }

  getContextValue() {
    return {
      toggle: this.toggle,
      isOpen: this.props.isOpen,
      direction: this.props.direction === "down" && this.props.dropup ? "up" : this.props.direction,
      disabled: this.props.disabled
    }
  }

  componentDidMount() {
    this.handleProps()
  }

  componentDidUpdate(prevProps) {
    if (this.props.isOpen !== prevProps.isOpen) {
      this.handleProps()
    }
  }

  componentWillUnmount() {
    this.removeEvents()
  }

  getContainer() {
    return this.containerRef.current
  }

  getMenuCtrl() {
    if (this._$menuCtrl) return this._$menuCtrl
    this._$menuCtrl = this.getContainer().querySelector("[aria-expanded]")
    return this._$menuCtrl
  }

  getMenuItems() {
    return [].slice.call(this.getContainer().querySelectorAll('[role="menuitem"]'))
  }

  addEvents() {
    ["click", "touchstart", "keyup"].forEach(event =>
      document.addEventListener(event, this.handleDocumentClick, true)
    )
  }

  removeEvents() {
    ["click", "touchstart", "keyup"].forEach(event =>
      document.removeEventListener(event, this.handleDocumentClick, true)
    )
  }

  handleDocumentClick(e) {
    if (e && (e.which === 3 || (e.type === "keyup" && e.which !== keyCodes.tab))) return
    const container = this.getContainer()

    if (
      container.contains(e.target) &&
      container !== e.target &&
      (e.type !== "keyup" || e.which === keyCodes.tab)
    ) {
      return
    }

    this.toggle(e)
  }

  handleKeyDown(e) {
    if (
      /input|textarea/i.test(e.target.tagName) ||
      (keyCodes.tab === e.which &&
        (e.target.getAttribute("role") !== "menuitem" || !this.props.a11y))
    ) {
      return
    }

    if (preventDefaultKeys.indexOf(e.which) !== -1 || (e.which >= 48 && e.which <= 90)) {
      e.preventDefault()
    }

    if (this.props.disabled) return

    if (this.getMenuCtrl() === e.target) {
      if (
        !this.props.isOpen &&
        [keyCodes.space, keyCodes.enter, keyCodes.up, keyCodes.down].indexOf(e.which) > -1
      ) {
        this.toggle(e)
        setTimeout(() => this.getMenuItems()[0].focus())
      } else if (this.props.isOpen && e.which === keyCodes.esc) {
        this.toggle(e)
      }
    }

    if (this.props.isOpen && e.target.getAttribute("role") === "menuitem") {
      if ([keyCodes.tab, keyCodes.esc].indexOf(e.which) > -1) {
        this.toggle(e)
        this.getMenuCtrl().focus()
      } else if ([keyCodes.space, keyCodes.enter].indexOf(e.which) > -1) {
        e.target.click()
        this.getMenuCtrl().focus()
      } else if (
        [keyCodes.down, keyCodes.up].indexOf(e.which) > -1 ||
        ([keyCodes.n, keyCodes.p].indexOf(e.which) > -1 && e.ctrlKey)
      ) {
        const $menuitems = this.getMenuItems()
        let index = $menuitems.indexOf(e.target)
        if (keyCodes.up === e.which || (keyCodes.p === e.which && e.ctrlKey)) {
          index = index !== 0 ? index - 1 : $menuitems.length - 1
        } else if (keyCodes.down === e.which || (keyCodes.n === e.which && e.ctrlKey)) {
          index = index === $menuitems.length - 1 ? 0 : index + 1
        }
        $menuitems[index].focus()
      } else if (keyCodes.end === e.which) {
        const $menuitems = this.getMenuItems()
        $menuitems[$menuitems.length - 1].focus()
      } else if (keyCodes.home === e.which) {
        const $menuitems = this.getMenuItems()
        $menuitems[0].focus()
      } else if (e.which >= 48 && e.which <= 90) {
        const $menuitems = this.getMenuItems()
        const charPressed = String.fromCharCode(e.which).toLowerCase()
        for (let i = 0; i < $menuitems.length; i += 1) {
          const firstLetter =
            $menuitems[i].textContent && $menuitems[i].textContent[0].toLowerCase()
          if (firstLetter === charPressed) {
            $menuitems[i].focus()
            break
          }
        }
      }
    }
  }

  handleProps() {
    if (this.props.isOpen) {
      this.addEvents()
    } else {
      this.removeEvents()
    }
  }

  toggle(e) {
    if (this.props.disabled) {
      return e && e.preventDefault()
    }

    return this.props.toggle(e)
  }

  render() {
    const {
      setActiveFromChild,
      addonType,
      className,
      cssModule,
      direction,
      active,
      isOpen,
      group,
      size,
      nav,
      tag,
      ...attrs
    } = omit(this.props, ["toggle", "disabled", "a11y"])

    const Tag = tag || (nav ? "div" : "div")

    let subItemIsActive = false
    if (setActiveFromChild) {
      React.Children.map(this.props.children[1].props.children, dropdownItem => {
        if (dropdownItem && dropdownItem.props.active) subItemIsActive = true
      })
    }

    const classes = mapToCssModules(
      classNames(
        className,
        direction !== "down" && `drop${direction}`,
        nav && active ? "active" : false,
        setActiveFromChild && subItemIsActive ? "active" : false,
        {
          [`input-group-${addonType}`]: addonType,
          "btn-group": group,
          [`btn-group-${size}`]: !!size,
          dropdown: !group && !addonType,
          show: isOpen,
          "nav-item": nav
        }
      ),
      cssModule
    )

    return (
      <DropdownContext.Provider value={this.getContextValue()}>
        <Manager>
          <Tag
            {...attrs}
            {...{
              [typeof Tag === "string" ? "ref" : "innerRef"]: this.containerRef
            }}
            onKeyDown={this.handleKeyDown}
            className={classes}
          />
        </Manager>
      </DropdownContext.Provider>
    )
  }
}

class DropdownUncontrolled extends React.Component {
  constructor(props) {
    super(props)

    this.state = { isOpen: props.defaultOpen || false }
    this.toggle = this.toggle.bind(this)
  }

  toggle(e) {
    this.setState({ isOpen: !this.state.isOpen }, () => {
      if (this.props.onToggle) {
        this.props.onToggle(e, this.state.isOpen)
      }
    })
  }

  render() {
    return (
      <Dropdown isOpen={this.state.isOpen} toggle={this.toggle} {...omit(this.props, omitKeys)} />
    )
  }
}

DropdownUncontrolled.propTypes = {
  defaultOpen: PropTypes.bool,
  onToggle: PropTypes.func,
  ...Dropdown.propTypes
}

Dropdown.propTypes = propTypes
Dropdown.defaultProps = defaultProps

Dropdown.Item = DropdownItem
Dropdown.Menu = DropdownMenu
Dropdown.Submenu = DropdownSubmenu
Dropdown.SubmenuMenu = DropdownSubmenuMenu
Dropdown.Toggle = DropdownToggle
Dropdown.Header = DropdownHeader
Dropdown.Divider = DropdownDivider
Dropdown.Row = DropdownRow
Dropdown.Col = DropdownCol
Dropdown.Uncontrolled = DropdownUncontrolled

export default Dropdown
